import asyncio
from bleak import BleakClient

DEVICE_ADDRESS = "3D:6E:57:11:F2:42"
WRITE_CHAR_UUID = "00001337-0000-1000-8000-00805f9b34fb"
NOTIFY_CHAR_UUID = "00001337-0000-1000-8000-00805f9b34fb"


# Notification handler
def notification_handler(sender, data):
    print(f"Received response: {data.hex()}")


async def send_to_display():
    try:
        async with BleakClient(DEVICE_ADDRESS, timeout=60) as client:
            print(f"Connected to {DEVICE_ADDRESS}")

            # Enable notifications
            await client.start_notify(NOTIFY_CHAR_UUID, notification_handler)
            print("Notifications enabled - waiting for responses...")

            # Send initialization commands
            await client.write_gatt_char(WRITE_CHAR_UUID, bytes.fromhex("0000"), response=False)
            await client.write_gatt_char(WRITE_CHAR_UUID, bytes.fromhex("020000"), response=False)

            # Prepare image data (replace with your actual image data)
            # This is just a placeholder - use your real image hex data
            image_hex = "" #Replace with actual image data from your HTML
            image_hex = image_hex.replace("\n", "").replace(" ", "").replace(",", "")

            # Send image data in chunks of 480 hex chars (240 bytes)
            chunk_size = 480
            upload_part = 0
            start_time = asyncio.get_event_loop().time()

            while len(image_hex) > 0:
                chunk = image_hex[:chunk_size]
                image_hex = image_hex[chunk_size:]

                # Create the command (0x03 prefix + chunk)
                cmd = bytes.fromhex("03" + chunk)
                await client.write_gatt_char(WRITE_CHAR_UUID, cmd, response=False)

                print(f"Sent part {upload_part}, remaining: {len(image_hex)} chars")
                upload_part += 1

                # Small delay between chunks if needed
                await asyncio.sleep(0.01)

            # Send completion command
            await client.write_gatt_char(WRITE_CHAR_UUID, bytes.fromhex("01"), response=False)
            elapsed_time = asyncio.get_event_loop().time() - start_time
            print(f"Image data sent in {elapsed_time:.2f} seconds")

            # Wait a bit to receive any responses
            await asyncio.sleep(5)

            # Disable notifications before disconnecting
            await client.stop_notify(NOTIFY_CHAR_UUID)

    except Exception as e:
        print(f"Error: {e}")


asyncio.run(send_to_display())